@import url('https://fonts.googleapis.com/css?family=Lato:300,400|Spectral');

section {
    background: #f9f9f9;
    padding: 102px 82px;
    min-height: 400px;
    margin-right: auto;
    margin-left: auto;
    max-width: 1170px;
    width: 100%;
    justify-items: center;
  .cont {    
    display: grid;
    grid-template-rows: 1fr 1fr;
    .logos {
    display: grid;
    grid-auto-flow: column;
    padding-top: 3rem;
}
}
}
h2 {
    color: #757575;
    font-size: 10px;
    letter-spacing: 5px;
    text-transform: uppercase;
    font-family: 'Spectral', serif;
}
h3 {
        color: #19191f;
        font-size: 27px;
        line-height: 40px;
        font-weight: normal;
        margin-top: 0;
        width: 488px;
        height: 0;
        font-family: 'Spectral', serif;
 }
 @media screen (min-width: 600px;) {
     section .cont {
        grid-auto-flow: row;
     }
 }

@import url('https://fonts.googleapis.com/css2?family=Source+Sans+Pro&display=swap');

section {
  width: 100%;
  margin-right: auto;
  margin-left: auto;
  padding: 2rem 2rem 3rem 2rem;
  background-color: rgb(234 234 234);
  max-width: 80%;
  margin-top: 2rem;
  .cont {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    gap: 2rem;
    .hed {
      color: black;
      .texy {
        line-height: 0.5rem;
        padding-bottom: 1.5rem;
      }
    }
  }
  .head {
		font-family: 'Source Sans Pro', sans-serif;
    font-weight: 200;
    padding-bottom: 1rem;
    font-size: 1.5rem;
    line-height: 2rem;
    letter-spacing: 0.2rem;
  }
  .txt {
    font-size: 1.25rem;
    line-height: 1.75rem;
  }
  i {
    padding-left: 1rem;
    padding-right: 1rem;
  }
  input {
    border-width: 0 0 2px 0;
    border-color: rgba(107, 114, 128);
    background-color: rgba(229, 231, 235);
    width: 100%;
    margin-bottom: 10px;
    margin-top: 15px;
  }
}

footer {
  background-color: rgba(17, 24, 39);	
  color: white;
  border-top: 2px solid rgba(31, 41, 55);	
  padding: 2rem 0;
  transition: color 0.1s, opacity 0.1s;

  .container {

    .body {
      max-width: 600px;

      .title {
        font-size: 1.125rem;
				text-transform: uppercase;
      	font-weight: 600;
      }

      .subtitle {
        font-size: 0.875rem;
				color: rgba(156, 163, 175);	
      	margin: 1rem 0;
      }

      ul {
        display: flex;
      	flex-wrap: wrap;
      	margin-bottom: 0.5rem;

        li {
          font-size: 0.875rem;
					color: rgba(156, 163, 175);	
          text-decoration: underline;
          margin-right: 1rem;
        }
      }
    }

		.primo {
			font-size: 0.75rem;
  		font-weight: bold;
  		&:hover { color:rgb(248,68,73) }
    }

    .social-links {
      border-bottom: 1px solid rgba(17, 24, 39);	

      .heading {
        font-size: 1.25rem;
				line-height: 1.75rem;
      	font-weight: bold;
      	margin-bottom: 1.5rem;
      }

      ul {
        display: flex;
        color: white;

        li {
      		font-size: 2.25rem;
          margin-right: 1rem;

          &:hover {opacity:0.8}
        }
      }
